$(function(){if($("body").attr("id")=="dashboard"){var a=$(".add-class-btn");var b=$("form.add-class");if($(".display").children().length==2){show_form()}b.on("submit",function(c){c.preventDefault();$.post("resources/library/actions.php",{action:"add",type:"class",data:{name:$(this).find("input[name='class_name']").val(),desc:$(this).find("input[name='class_desc']").val(),color:$(this).find(".colors input:checked").val()}},function(d){if(d==true){location.reload()}})});b.find(".colors").on("mouseup",function(d){var c=$(d.target).attr("for");b.parent().attr("color",c)});$("body").on("click",".add-class-btn",function(c){show_form()});$("body").on("click",".display .new .close-btn",function(c){hide_form()});$("body").on("click",".display .class .close-btn",function(d){d.preventDefault();var c=$(d.target).parent().parent().attr("class-id");$.post("resources/library/actions.php",{action:"delete",type:"class",id:c},function(e){if(e==true){$(d.target).parent().parent().remove();if($(".display").children().length==2){show_form()}}return false})})}});function show_form(){var a=$(".add-class-btn");var b=$("form.add-class");a.attr("hide","true");b.parent().attr("hide","false");a.after(b.parent());b.find('input[name="class_name"]').focus();$(".display").scrollLeft($(".display").width())}function hide_form(){var a=$(".add-class-btn");var b=$("form.add-class");b.parent().attr("hide","true");a.attr("hide","false");b.parent().after(a);b.find('input[name="class_name"]').val("");b.find('input[name="class_desc"]').val("");b.parent().removeAttr("color")};